

def getFloor(input):
    floor = 0
    for c in input:
        if c == '(':
            floor += 1
        else:
            floor -= 1
    return floor

def getPosition(input):
    floor = 0
    pos = 1
    for c in input:
        if c == '(':
            floor += 1
        else:
            floor -= 1
        if floor == -1:
            return pos
        else:
            pos += 1
    return pos

